import { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';

function SearchBar() {
const [canSendData, setCanSendData]=useState(false)
const [ErrorMessage, setErrorMessage]=useState("")
const validateURL=(url)=>{
const urlPattern =/https?:\/\/[^\s/$.?#].[^\s]*/g; 
if(urlPattern.test(url)){
    setCanSendData(true)
    setErrorMessage("")
  }else{
    setErrorMessage("please enter again !")
    setCanSendData(false)
  }
  }


  // Use the match method to find all email addresses in the text

  return (
    <Box>
       <FormControl error variant="standard">
      <TextField 
      error={ErrorMessage.length>0}
       onChange={(event) => {
        validateURL(event.target.value);
      }}
      id="outlined-basic" label="Enter website URL" variant="outlined" 
      sx={{ margin: 2, width: '80vh' }} />
      <FormHelperText >{ErrorMessage}</FormHelperText>
      </FormControl>
      <Stack spacing={2} direction="row"sx={{ margin: 2, width: '80vh' ,alignItems: 'center',padding: 1}}>

        <Button variant="outlined" disabled={!canSendData}>Summarize</Button>
        <Button variant="outlined" disabled={!canSendData}>Roast</Button>
      </Stack>
    </Box>
  );
}

export default SearchBar;
