import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import Skeleton from '@mui/material/Skeleton';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';

// Import the necessary functions from './req'
import { summarizeData, roast } from './req';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

function SearchBar() {
  const [url, setUrl] = React.useState("");
  const [language, setLanguage] = React.useState("");
  const [canSendData, setCanSendData] = React.useState(false);
  const [errorMessage, setErrorMessage] = React.useState("");
  const [response, setResponse] = React.useState("");
  const [isLoading, setIsLoading] = React.useState(false);
  const [openSnackbar, setOpenSnackbar] = React.useState(false);

  const handleSummarize = async () => {
    setIsLoading(true);
    setOpenSnackbar(true); // Open the Snackbar when fetching data
    const data = { language: language, url: url };
    const result = await summarizeData(data);
    setResponse(result.data);
    setIsLoading(false);
    setOpenSnackbar(false); // Close the Snackbar when the response appears
  };

  const handleRoast = () => {
    setIsLoading(true);
    setOpenSnackbar(true); // Open the Snackbar when fetching data
    const data = { language: language, url: url };
    const response = roast(data);
    setIsLoading(false);
    setResponse(response);
        setOpenSnackbar(false); // Close the Snackbar when the response appears
  };

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
  };

  const validateURL = (url) => {
    const urlPattern = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w\.-]*)*\/?$/;
    if (urlPattern.test(url)) {
      setCanSendData(true);
      setErrorMessage("");
      setUrl(url);
    } else {
      setErrorMessage("Please enter a valid URL");
      setCanSendData(false);
    }
  };

  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenSnackbar(false);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column'
      }}
    >
      <FormControl error variant="standard">
        <Box
          sx={{
            display: 'flex',
            alignItems: 'center',
            flexDirection: 'align',
          }}
        >
          <TextField
            error={errorMessage.length > 0}
            onChange={(event) => {
              validateURL(event.target.value);
            }}
            id="outlined-basic"
            label="Enter Website URL"
            variant="outlined"
            sx={{
              outlineColor: '#002B40',
              width: '70vh',
            }}
          ></TextField>

          <FormControl style={{ width: '30%' }}>
            <InputLabel>Language</InputLabel>
            <Select value={language} label="langue" onChange={handleLanguageChange}>
              <MenuItem value=""><em>None</em></MenuItem>
              <MenuItem value={'fr'}>French</MenuItem>
              <MenuItem value={'en'}>English</MenuItem>
            </Select>
          </FormControl>
        </Box>
        <FormHelperText id="component-error-text">{errorMessage}</FormHelperText>
      </FormControl>

      <Stack spacing={2} direction="row" sx={{ marginTop: 2, marginBottom: 5 }}>
        <Button variant="contained" disabled={!canSendData} onClick={handleSummarize}>
          Summarize
        </Button>
        <Button variant="outlined" disabled={!canSendData} onClick={handleRoast}>
          Roast
        </Button>
      </Stack>

      <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="info">
          Fetching data...
        </Alert>
      </Snackbar>

      {isLoading ? (
        <Box sx={{}}>
          <Skeleton animation="wave" width={1000} height={20} sx={{ margin: 1 }} />
          <Skeleton animation="wave" width={1000} height={20} sx={{ margin: 1 }} />
          <Skeleton animation="wave" width={1000} height={20} sx={{ margin: 1 }} />
          <Skeleton animation="wave" width={1000} height={20} sx={{ margin: 1 }} />
        </Box>
      ) : response ? (
        <Box sx={{
          display: 'flex',
          alignItems: 'center',
          flexDirection: 'column',
          marginLeft: 40,
          marginRight: 40
        }}
        >
          {response}
        </Box>
      ) : null}
    </Box>
  );
}

export default SearchBar;
