import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import { useState  } from 'react';
import FormControl from '@mui/material/FormControl';
import FormHelperText from '@mui/material/FormHelperText';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import { summarizeData,roast } from './req';

function SearchBar() {

  const handleSummarize = () => {
    const data=[{ language:language },
      { url : url}];
    summarizeData();
  };

  const handleRoast = () => {
    const data=[{ language:language },
    { url : url}];
    roast(data);
  };
const [url, setUrl ] = useState("")

const [language, setLanguage] = useState("")
const handleLanguageChange = (event) => {
  setLanguage(event.target.value);
};
const[canSendData, setCanSendData]=useState(false)
const [errorMessage,setErrorMessage] = useState("")
const validateURL=(url)=>{
  const urlPattern = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w\.-]*)*\/?$/;
if(urlPattern.test(url)){
  setCanSendData(true)
  setErrorMessage("")
}else{
  setErrorMessage("Please enter a valid website URL")
  setCanSendData(false)
}
}
  return (
  <Box  sx={{
  
    display : 'flex',
    alignItems:'center',
    
    flexDirection: 'column'

    
      }}>

<FormControl error variant="standard">

    <Box sx={{
  
  display : 'flex',
  alignItems:'center',
  
  flexDirection: 'align'

  
    }}>
      
      <TextField error={errorMessage.length>0}
onChange={(event) => {
        validateURL(event.target.value);
      }}
   id="outlined-basic" label="Enter Website URL" variant="outlined" sx={{
  outlineColor:'#002B40',
  width:'70vh'}}> 

</TextField>



  <FormControl style={{ width: '30%' }} >
        <InputLabel >Language</InputLabel>
        <Select
          value={language}
    label="langue"
    onChange={handleLanguageChange}>
        <MenuItem value=""><em>None</em></MenuItem>        
        <MenuItem value={"fr"}>French</MenuItem>
        <MenuItem value={"en"}>English</MenuItem>
        </Select>
      </FormControl>

  </Box>
<FormHelperText id="component-error-text">{errorMessage}</FormHelperText>

</FormControl>
<Stack spacing={2} direction="row" sx={{
  
  marginTop:5
  
    }}>

<Button variant="contained" disabled={!canSendData} onClick={handleSummarize}>
        Summarize
      </Button>
      <Button variant="contained" disabled={!canSendData} onClick={handleRoast}>
        Roast
      </Button>
</Stack>

  </Box>


  );
}

export default SearchBar;
